mapper.select:

select count(*) 
from tb_user 
where user_email = #{user_email}


select count(*) 
from tb_user 
where user_nickname = #{user_nickname}

select user_email, user_pw, user_nickname, user_name
from tb_user 
where user_email = #{user_email} and user_pw = #{user_pw}

mapper.update:
UPDATE tb_user SET 
user_pw = #{user_pw},
user_nickname = #{user_nickname},
user_pnum = #{user_pnum}
WHERE user_email = #{user_email}
